def generateopenaispeech(text, voice="alloy", model="tts-1"):
    response = requests.post(
        "https://api.openai.com/v1/audio/speech",
        headers={"Authorization": f"Bearer {openaikey}"},
        json={"model": model, "input": text, "voice": voice}
    )
    return response.content
def generategeminispeech(text, voicename="en-US-Neural2-A"):
    response = requests.post(
        f"https://texttospeech.googleapis.com/v1/text:synthesize?key={googlekey}",
        json={
            "input": {"text": text},
            "voice": {"languageCode": "en-US", "name": voicename},
            "audioConfig": {"audioEncoding": "MP3"}
        }
    )
    return base64.b64decode(response.json()["audioContent"])
